#include <stdio.h>
#include "main.h"

int main(int argc, char const *argv[])
{
    int inputLength = sizeof(INPUT) / sizeof(INPUT[0]);
    CalculateFinalFloor(inputLength);
    GetFirstBasement(inputLength);
    char input;
    scanf("%c", &input);
    return 0;
}

void GetFirstBasement(int inputLength){
    int floor = 0;
    for (int i = 0; i < inputLength; i++)
    {
        if (floor == -1)
        {
            printf("First basement %d\n", i);
            break;
        }

        if (INPUT[i] == '(')
        {
            floor++;
        }
        else if (INPUT[i] == ')')
        {
            floor--;
        }
    }
    

}

void CalculateFinalFloor(int inputLength)
{
    int up = 0;
    int down = 0;
    for (int i = 0; i < inputLength; i++)
    {
        if (INPUT[i] == '(')
        {
            up++;
        }
        else if (INPUT[i] == ')')
        {
            down++;
        }
    }

    int finalFloor = up - down;
    printf("Final Floor : %d\n", finalFloor);
}


